import React from "react";
import "./App.css";
import Mastercard from "./mastercard.png";
import Sbi from "./sbi.png";
import Chip from './chip.png'
import Wifi from './wifi.png'

function Card() {
  return (
    <section className="card-section">
      <div className="front-face">
        <header>
          <h2 style={{marginLeft:20}}>gold</h2>
            <img src={Sbi} id="sbi-logo" alt="sbi logo" width={60} />
        </header>

            <h2 className="number">5515 9900 0511 5919</h2>
        <div className="details">
            <h3 className="name" style={{paddingLeft:40}}>Sarath Donepudi</h3>
            {/* <img src={Wifi} id="wifi" alt="wifi" width={70} /> */}
            <h1 style={{paddingRight:30, fontSize:40}}>â–¸</h1>

        </div>

        <div className="last-line">
            <img src={Mastercard} id="mastercard-logo" alt="mastercard logo" width={80}/>
            <h4>VALID THRU 07/25</h4>
            <img src={Chip} id="chip" alt="chip" width={70} />
        </div>
      </div>


      <div className="back-face">
        <h5 style={{marginBottom:10,paddingLeft:20,paddingRight:20}}>For customer service, call 1800 900 8989 or mail help@mastercard.com</h5>
        <span className="magnetic-strip"></span>
        <div className="signature"><i>005</i></div>
        <p>Lorem Ipsum Lorem Ipsum Lorem Ipsum Lorem Ipsum Lorem Ipsum Lorem Ipsum Lorem Ipsum Lorem Ipsum Lorem Ipsum Lorem Ipsum Lorem Ipsum</p>
      </div>

    </section>
  );
}

export default Card;
